) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='发布作业表';-- <xjx> --

-- ±íµÄ½á¹¹£ºxh_sxsetcourse --
CREATE TABLE `xh_sxsetcourse` (
  `scid` int(10) NOT NULL AUTO_INCREMENT,
  `jsno` varchar(9) NOT NULL,
  `ccode` varchar(6) NOT NULL,
